// Check if we have leads to process
if (!items || items.length === 0) {
  console.log('No unenriched leads found');
  return [];
}

console.log(`Found ${items.length} leads to enrich`);
return items;

// Create Mistral prompt for lead enrichment
const lead = items[0].json;

const prompt = `Analyze this social media lead and provide enrichment data:

Lead Information:
- Name: ${lead.name}
- Username: ${lead.username}
- Bio: ${lead.bio}
- Follower Count: ${lead.follower_count}
- Verified: ${lead.verified}
- Location: ${lead.location}
- Source: ${lead.source}

Please analyze this lead and respond with ONLY a valid JSON object in this exact format:
{
  "niche": "primary business niche/industry (e.g., 'technology', 'fitness', 'finance', 'marketing')",
  "score": engagement_quality_score_0_to_100,
  "notes": "30-word summary of lead quality and potential",
  "message": "personalized outreach message (50-80 words)",
  "compliant": true_or_false,
  "compliance_notes": "brief compliance assessment",
  "ai_confidence": confidence_score_0_to_1
}

Consider:
- Follower count and engagement potential
- Bio relevance to business/professional content
- Verification status
- Industry alignment
- Compliance with outreach regulations

Respond with ONLY the JSON object, no other text.`;

return [{
  json: {
    lead_id: lead.id,
    lead_name: lead.name,
    mistral_prompt: prompt
  }
}];

// Parse Mistral response and prepare for database insertion
const lead_id = $node['Prepare Mistral Prompt'].json.lead_id;
const lead_name = $node['Prepare Mistral Prompt'].json.lead_name;

// Debug: Log the full response structure
console.log('Full Mistral Response:', JSON.stringify($json, null, 2));

// Handle different possible response structures
let mistralResponse;

// Check for error responses
if ($json.error) {
  console.error('Mistral API error:', $json.error);

  return [{
    json: {
      lead_id: lead_id,
      niche: 'general',
      score: 50,
      notes: 'Mistral API error - check API key and credits',
      message: `Hi ${lead_name || 'there'}, I noticed your profile and thought you might be interested in our services.`,
      compliant: false,
      compliance_notes: 'API error - manual review required',
      ai_confidence: 0.1,
      enrichment_version: '1.0'
    }
  }];
}

// Standard OpenRouter API response structure
if ($json.choices && $json.choices[0] && $json.choices[0].message) {
  mistralResponse = $json.choices[0].message.content;
} else if ($json.content) {
  mistralResponse = $json.content;
} else if ($json.message) {
  mistralResponse = $json.message;
} else {
  console.error('Unable to find Mistral response content in expected locations');
  console.error('Available keys in response:', Object.keys($json));
  mistralResponse = null;
}

console.log('Extracted Mistral Response:', mistralResponse);

if (!mistralResponse) {
  console.error('No valid Mistral response found');
  // Return fallback enrichment
  return [{
    json: {
      lead_id: lead_id,
      niche: 'general',
      score: 50,
      notes: 'Mistral response structure error - requires manual review',
      message: `Hi ${lead_name || 'there'}, I noticed your profile and thought you might be interested in our services.`,
      compliant: false,
      compliance_notes: 'Response structure error - manual review required',
      ai_confidence: 0.1,
      enrichment_version: '1.0'
    }
  }];
}

try {
  // Clean the response to extract JSON
  let cleanResponse = mistralResponse.trim();

  // Remove markdown code blocks if present
  if (cleanResponse.startsWith('```json')) {
    cleanResponse = cleanResponse.replace(/```json\s*/, '').replace(/\s*```$/, '');
  }
  if (cleanResponse.startsWith('```')) {
    cleanResponse = cleanResponse.replace(/```\s*/, '').replace(/\s*```$/, '');
  }

  // Find JSON object in the response
  const jsonMatch = cleanResponse.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    cleanResponse = jsonMatch[0];
  }

  console.log('Cleaned response for parsing:', cleanResponse);

  // Parse the JSON response from Mistral
  const enrichmentData = JSON.parse(cleanResponse);

  // Validate required fields
  const requiredFields = ['niche', 'score', 'notes', 'message'];
  for (const field of requiredFields) {
    if (!enrichmentData.hasOwnProperty(field)) {
      throw new Error(`Missing required field: ${field}`);
    }
  }

  // Ensure score is within range
  enrichmentData.score = Math.max(0, Math.min(100, parseInt(enrichmentData.score)));

  // Ensure ai_confidence is within range
  if (enrichmentData.ai_confidence) {
    enrichmentData.ai_confidence = Math.max(0, Math.min(1, parseFloat(enrichmentData.ai_confidence)));
  } else {
    enrichmentData.ai_confidence = 0.8; // Default confidence
  }

  return [{
    json: {
      lead_id: lead_id,
      niche: enrichmentData.niche,
      score: enrichmentData.score,
      notes: enrichmentData.notes,
      message: enrichmentData.message,
      compliant: enrichmentData.compliant !== false, // Default to true if not specified
      compliance_notes: enrichmentData.compliance_notes || 'Automated assessment',
      ai_confidence: enrichmentData.ai_confidence,
      enrichment_version: '1.0'
    }
  }];

} catch (error) {
  console.error('Error parsing Mistral response:', error);
  console.error('Raw response that failed to parse:', mistralResponse);

  // Fallback enrichment if parsing fails
  return [{
    json: {
      lead_id: lead_id,
      niche: 'general',
      score: 50,
      notes: 'Automatic enrichment failed - requires manual review',
      message: `Hi ${lead_name || 'there'}, I noticed your profile and thought you might be interested in our services.`,
      compliant: false,
      compliance_notes: 'Parse error - manual review required',
      ai_confidence: 0.1,
      enrichment_version: '1.0'
    }
  }];
}

// Log enrichment completion
const enrichedCount = items.length;
console.log(`Successfully enriched ${enrichedCount} leads`);

return [{
  json: {
    message: 'Lead enrichment completed',
    enriched_count: enrichedCount,
    timestamp: new Date().toISOString()
  }
}];
